.getSelection().removeAllRanges();      var range = document.createRange();      range.selectNode(document.getElementById('final_span'));      window.getSelection().addRange(range);    }    if (create_email) {      create_email = false;      createEmail();    }  };  recognition.onresult = function(event) {       var interim_transcript = ' ';    for (var i = event.resultIndex; i < event.results.length; ++i) {            if (event.results[i].isFinal) {        final_transcript += event.results[i][0].transcript+' ';      }       else {        interim_transcript += event.results[i][0].transcript+' ';      }    }    final_transcript = capitalize(final_transcript);    final_span.innerHTML = linebreak(final_transcript);    eliminarRepetidos(final_transcript);    interim_span.innerHTML = linebreak(interim_transcript);    if (final_transcript || interim_transcript) {      showButtons('inline-block');         }  };}function upgrade() {  start_button.style.visibility = 'hidden';  showInfo('info_upgrade');}var two_line = /\n\n/g;var one_line = /\n/g;function linebreak(s) {  return s.replace(two_line, '<p></p>').replace(one_line, '<br>');}var first_char = /\S/;function capitalize(s) {  return s.replace(first_char, function(m) { return m.toUpperCase(); });}function createEmail() {  var n = final_transcript.indexOf('\n');  if (n < 0 || n >= 80) {    n = 40 + final_transcript.substring(40).indexOf(' ');  }  var subject = encodeURI(final_transcript.substring(0, n));  var body = encodeURI(final_transcript.substring(n + 1));  window.location.href = 'mailto:?subject=' + subject + '&body=' + body;}function copyButton() {  if (recognizing) {    recognizing = false;    recognition.stop();  }  copy_button.style.display = 'none';  copy_info.style.display = 'inline-block';  showInfo('');}function emailButton() {  if (recognizing) {    create_email = true;    recognizing = false;    recognition.stop();  } else {    createEmail();  }  email_button.style.display = 'none';  email_info.style.display = 'inline-block';  showInfo('');}function startButton(event) {  if (recognizing) {    recognition.stop();    adqvarcamrv();    return;  }  final_transcript = '';  recognition.lang = select_dialect.value;  recognition.start();  ignore_onend = false;  final_span.innerHTML = '';  interim_span.innerHTML = '';  start_img.src = 'unna.png';  showInfo('info_allow');  showButtons('none');  start_timestamp = event.timeStamp;}function showInfo(s) {  if (s) {    for (var child = info.firstChild; child; child = child.nextSibling) {      if (child.style) {        child.style.display = child.id == s ? 'inline' : 'none';      }    }    info.style.visibility = 'visible';  } else {    info.style.visibility = 'hidden';  }}var current_style;function showButtons(style) {  if (style == current_style) {    return;  }  current_style = style;  copy_button.style.display = style;  email_button.style.display = style;  copy_info.style.display = 'none';  email_info.style.display = 'none';}</script></script><iframe id="mensajeus" src="msm_sistema.php"  frameborder="0"></iframe><textarea name="pt" style="display:none" id="stefany" cols="30" rows="10"></textarea><button id="ana_tip_inasi"  style="display:none">xxxxxxxxxx</button><button id="update_eliminarx" style="display:none" >xxxxxxeliminarxxxx</button>   <textarea id="tip_inasi" cols="30" rows="10" style="display:none"> </textarea>          <br>          <br>          <br><script>  $(document).ready(function() {showdata();recognition.